    positions[i] = Math.random() - 0.5

   return  <mesh>
        <bufferGeometry> 
            <bufferAttribute
            attach='attributes-position'
            count={verticiesCount}
            itemSize
            />
         </bufferGeometry> 
        <meshBasicMaterial color='red'/>
    </mesh>
}
